create table category ( id int ( 11 ) not null auto_increment, owner int ( 11 ) not null, title tinytext not null, body text not null, created datetime ( 6 ) default current_timestamp ( 6 ), edited datetime ( 6 ) null default null on update current_timestamp ( 6 ), primary key ( id ), foreign key ( owner ) references user ( id ) ) engine = innodb auto_increment=0;

create table topic ( id int ( 11 ) not null auto_increment, owner int ( 11 ) not null, category int ( 11 ) not null, title tinytext not null, body text not null, created datetime ( 6 ) default current_timestamp ( 6 ), edited datetime ( 6 ) null default null on update current_timestamp ( 6 ), primary key ( id ), foreign key ( owner ) references user ( id ), foreign key ( category ) references category ( id ) ) engine = innodb auto_increment=0;

create table post ( id int ( 11 ) not null auto_increment, owner int ( 11 ) not null, category int ( 11 ) null default null, topic int ( 11 ) null default null, chat int ( 11 ) null default null, body text not null, created datetime ( 6 ) default current_timestamp ( 6 ), edited datetime ( 6 ) null default null on update current_timestamp ( 6 ), primary key ( id ), foreign key ( owner ) references user ( id ), foreign key ( topic ) references topic ( id ), foreign key ( category ) references category ( id ), foreign key ( chat ) references chat ( id ) ) engine = innodb auto_increment=0;

select TABLE_NAME,COLUMN_NAME,CONSTRAINT_NAME, REFERENCED_TABLE_NAME,REFERENCED_COLUMN_NAME from INFORMATION_SCHEMA.KEY_COLUMN_USAGE where REFERENCED_TABLE_NAME = 'topic';